import React from 'react'
import './post.css'
import { Rating ,} from '@mui/material'
import { Avatar } from '@mui/material';
import {MoreVert} from '@mui/icons-material'
import SentimentVeryDissatisfiedIcon from '@mui/icons-material/SentimentVeryDissatisfied';
import SentimentDissatisfiedIcon from '@mui/icons-material/SentimentDissatisfied';
import SentimentSatisfiedIcon from '@mui/icons-material/SentimentSatisfied';
import SentimentSatisfiedAltIcon from '@mui/icons-material/SentimentSatisfiedAltOutlined';
import SentimentVerySatisfiedIcon from '@mui/icons-material/SentimentVerySatisfied';
import {Users} from '../../dummyData'
import { useState,useEffect } from 'react';

function Post({post}) {
    
    const customIcons = {
        1: {
          icon: <SentimentVeryDissatisfiedIcon />,
          label: 'Very Dissatisfied',
        },
        2: {
          icon: <SentimentDissatisfiedIcon />,
          label: 'Dissatisfied',
        },
        3: {
          icon: <SentimentSatisfiedIcon />,
          label: 'Neutral',
        },
        4: {
          icon: <SentimentSatisfiedAltIcon />,
          label: 'Satisfied',
        },
        5: {
          icon: <SentimentVerySatisfiedIcon />,
          label: 'Very Satisfied',
        },
      };

      function IconContainer(props){
          const [value,...others] = props 
          return <span {...others}>{customIcons[value].icon}</span>
      }

      IconContainer.propTypes={
        value: propType.number.isRequired,
      }

    const [like, setLike] = useState(post.like)
    const [isLiked,setIsLiked]=useState(false)
    const [users,setUsers] = useState(null)

    const clickHandler = () =>(

        setLike(isLiked ? like - 1 : like + 1 ),
        setIsLiked(!isLiked)
    )
    useEffect(() => {
        fetch('http://localhost:8000/Users')
        .then(res =>{
            return  res.json();
        })
            .then(data =>{
                setUsers(data);
                
            }
            )
    },[])
    

    return (users &&
        <div className="post">
            <div className="postWrapper">
                <div className="postTop">
                    <div className="postTopLeft">
                        <Avatar src={ users.filter(u=>u.id===post.userId)[0]?.ProfilePicture}/>
                        <span className="postUsername">{users.filter(u=>u.id===post.userId)[0]?.Username}</span>
                        <span className="postTime">{post.date}</span>
                    </div>
                    <div className="postTopRight">
                        <MoreVert/>
                    </div>
                </div>
                <div className="postCenter">
                    <span className="spaceText">{post.desc}</span>
                    <img className="postImg" src={post.photo} alt="one_piece"/>
                </div>
                <div className="postBottom">
                    <div className="postBottomLeft">
                        <img className="likeIcon"  src="/assets/images/like.png" onClick={clickHandler} alt=""/>
                        <img className="likeIcon" src="/assets/images/heart.png" onClick={clickHandler} alt=""/>
                        <span className="postlikeCounter">{like} people liked</span>
                        <button className="buttonComment"><span> Add comment</span></button>
                    </div>
                    <Rating
                        name="highlight-selected-only"
                        defaultValue={2}
                        IconContainerComponent={IconContainer}
                        highlightSelectedOnly
                            />
                    <div className="postBottomRight">
                        <span className="postCommentText">{post.comment} comments</span>
                    </div>
                    
                </div>
            </div>
            
        </div>
    )
}

export default Post
