import './Share.css'
import { Avatar } from '@mui/material';
import {PermMedia,Label,Room,EmojiEmotions} from '@mui/icons-material'
import { useState } from 'react';

function ShareComp() {

        const fileBtn = document.getElementById("realbtn");
        const customBtn = document.getElementById("fakebtn")
        if(fileBtn){
        customBtn.addEventListener("click",function(){
            fileBtn.click();
        })}

        const [msg,setMsg] = useState(null)
        const [photo,setPhoto] = useState(null)

        const msgSubmit = (e)=>{
            var today=new Date();
            var date = today.getDate()+'/'+today.getMonth()+'/'+today.getFullYear();
            const blog=[msg,photo,date]

            fetch('http://localhost:8000/Posts',{

                method:'POST',
                headers:{ "Content-Type":"application/json"},
                body:JSON.stringify(blog)
            })
            

        }

        const encodeImageFileAsURL=(element) => {
            // const preview = document.querySelector('img')
            var file = element.files[0];
            var reader = new FileReader();
            reader.onloadend = function() {
                setPhoto(reader.result);
            }
            reader.readAsDataURL(file);}
          

        
    
    return (
        <div className="share">
            <div className="shareWrapper">
                <div className="shareTop">
                    <Avatar src="/assets/images/1.png"/>
                    <input placeholder="What's in ur mind" className="shareInput"/>
                </div>
                {/* <hr className="shareHr"/> */}
                <input type="text" className="inputMsgBox" placeholder="Share your Feelings" onChange={(e)=>setMsg(e.target.value)}/>
                <div className="shareBottom">
                    <div className="shareOptions">
                        <div className="shareOption">
                            <input type="file" id="realbtn" hidden="hidden" onChange={(e)=>encodeImageFileAsURL(e.target)}/>
                            <button className="fileUploadBtn" id="fakebtn">
                            <PermMedia htmlColor="tomato" className="shareIcon"/>            
                            <span className="shareOptionText">Photo/Video</span></button>                    
                        </div>
                        <div className="shareOption">
                            <Label htmlColor="blue" className="shareIcon"/>
                            <span className="shareOptionText">Tag</span>
                        </div>
                        <div className="shareOption">
                            <Room  htmlColor="green" className="shareIcon"/>
                            <span className="shareOptionText">Location</span>
                        </div>
                        <div className="shareOption">
                            <EmojiEmotions htmlColor="goldenrod" className="shareIcon"/>
                            <span className="shareOptionText">Feelings</span>
                        </div>
                    </div>
                    <button className="shareButton" onClick={msgSubmit}>Share</button>
                </div>
            </div>
           
        </div>
    )
}

export default ShareComp
