import React from 'react'
import './post.css'
import { Slider } from '@mui/material'
import { Avatar } from '@mui/material';
import {MoreVert} from '@mui/icons-material'
import {Users} from '../../dummyData'
import { useState,useEffect } from 'react';

function Post({post}) {
    
    const [like, setLike] = useState(post.like)
    const [isLiked,setIsLiked]=useState(false)
    const [users,setUsers] = useState(null)

    const clickHandler = () =>(

        setLike(isLiked ? like - 1 : like + 1 ),
        setIsLiked(!isLiked)
    )
    useEffect(() => {
        fetch('http://localhost:8000/Users')
        .then(res =>{
            return  res.json();
        })
            .then(data =>{
                setUsers(data);
                
            }
            )
    },[])
    

    return (users &&
        <div className="post">
            <div className="postWrapper">
                <div className="postTop">
                    <div className="postTopLeft">
                        <Avatar src={ users.filter(u=>u.id===post.userId)[0]?.ProfilePicture}/>
                        <span className="postUsername">{users.filter(u=>u.id===post.userId)[0]?.Username}</span>
                        <span className="postTime">{post.date}</span>
                    </div>
                    <div className="postTopRight">
                        <MoreVert/>
                    </div>
                </div>
                <div className="postCenter">
                    <span className="spaceText">{post.desc}</span>
                    <img className="postImg" src={post.photo} alt="one_piece"/>
                </div>
                <div className="postBottom">
                    <div className="postBottomLeft">
                        <img className="likeIcon"  src="/assets/images/like.png" onClick={clickHandler} alt=""/>
                        <img className="likeIcon" src="/assets/images/heart.png" onClick={clickHandler} alt=""/>
                        <span className="postlikeCounter">{like} people liked</span>
                        <button className="buttonComment"><span> Add comment</span></button>
                    </div>
                    <div className="slider">
                    <Slider  defaultValue={50} aria-label="Default" valueLabelDisplay="auto" /></div>
                    <progress className="progressbar" max="100" value="40">40%</progress>
                    <div className="postBottomRight">
                        <span className="postCommentText">{post.comment} comments</span>
                    </div>
                    
                </div>
            </div>
            
        </div>
    )
}

export default Post
